Scenario.Script:
//

	var patches = 9;

	function createBlock(template, owner, angle, x0, z0, cols, rows, colspacing, rowspacing)
	{
		var rdx = rowspacing * -Math.sin(angle);
		var rdz = rowspacing * -Math.cos(angle);
		var cdx = colspacing * Math.cos(angle);
		var cdz = colspacing * -Math.sin(angle);

		for (var r = 0; r < rows; ++r)
		{
			for (var c = 0; c < cols; ++c)
			{
				var x = x0 + (c - (cols-1)/2) * cdx + r * rdx;
				var z = z0 + (c - (cols-1)/2) * cdz + r * rdz;
				var ent = Engine.AddEntity(template);

				var cmpPosition = Engine.QueryInterface(ent, IID_Position);
				cmpPosition.MoveTo(x, z);
				cmpPosition.SetYRotation(angle);

				var cmpOwnership = Engine.QueryInterface(ent, IID_Ownership);
				if (cmpOwnership)
					cmpOwnership.SetOwner(owner);
			}
		}
	}

	var mx = 192;
	var mz = 128;
	var sep = 46;
	for (var i = 0; i < 8; ++i)
	{
		createBlock("units/athen_infantry_spearman_e", 1, Math.PI/2, mx - sep - 6, mz, 16, 4, 2.1, 3.0);
		createBlock("units/athen_infantry_spearman_e", 2, 3*Math.PI/2, mx + sep + 6, mz, 16, 4, 2.1, 3.0);
		createBlock("units/athen_infantry_archer_b", 1, Math.PI/2, mx - sep, mz, 8, 1, 5.0, 3.0);
		createBlock("units/athen_infantry_archer_b", 1, Math.PI/2, mx - sep - 2, mz, 7, 1, 5.0, 3.0);
		createBlock("units/athen_infantry_archer_b", 2, 3*Math.PI/2, mx + sep, mz, 8, 1, 5.0, 3.0);
		createBlock("units/athen_infantry_archer_b", 2, 3*Math.PI/2, mx + sep + 2, mz, 7, 1, 5.0, 3.0);

		mz += 42;
	}

	//

Scenario.ScriptSettings:
